    private void txt_CodSatKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt_CodSatKeyTyped
        // TODO add your handling code here:
        char c = evt.getKeyChar();
        if(c < '0' || c > '9') evt.consume();
    }//GEN-LAST:event_txt_CodSatKeyTyped

    private void txt_CodProdKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt_CodProdKeyTyped
        // TODO add your handling code here:
        char c = evt.getKeyChar();
        if(Character.isLowerCase(c)) evt.setKeyChar(Character.toUpperCase(c));
    }//GEN-LAST:event_txt_CodProdKeyTyped

