    private void btn_GuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_GuardarActionPerformed
        // TODO add your handling code here:
        String cod_prod = txt_CodProd.getText().trim();
        String cod_sat = txt_CodSat.getText().trim();
        
        if(cod_prod.isEmpty() || cod_sat.isEmpty()) JOptionPane.showMessageDialog(this, "Datos Incorrectos", "Error", JOptionPane.ERROR_MESSAGE);
        else
        {
            if(fun.actualizaCodigoSat(cod_prod, cod_sat)) JOptionPane.showMessageDialog(this, "Codigo Actualizado", "Informacion", JOptionPane.INFORMATION_MESSAGE);
            else JOptionPane.showMessageDialog(this, "No se pudo actualizar", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btn_GuardarActionPerformed

    private void txt_CodProdKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt_CodProdKeyPressed
        // TODO add your handling code here:
        if(evt.getKeyCode() == KeyEvent.VK_ENTER)
        {
            txt_CodSat.requestFocus();
        }
    }//GEN-LAST:event_txt_CodProdKeyPressed

    private void txt_CodSatKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt_CodSatKeyPressed
        // TODO add your handling code here:
        if(evt.getKeyCode() == KeyEvent.VK_ENTER)
        {
            if(txt_CodProd.getText().isEmpty() || txt_CodSat.getText().isEmpty())
                JOptionPane.showMessageDialog(this, "Datos Incorrectos", "Error", JOptionPane.ERROR_MESSAGE);
            else 
            {
                if(fun.actualizaCodigoSat(txt_CodProd.getText(), txt_CodSat.getText())) JOptionPane.showMessageDialog(this, "Codigos Actualizados", "Informacion", JOptionPane.INFORMATION_MESSAGE);
                else JOptionPane.showMessageDialog(this, "No se pudo actualizar", "Error", JOptionPane.ERROR_MESSAGE);
            }
        }
    }//GEN-LAST:event_txt_CodSatKeyPressed

